mapper.select:
SELECT a.id, a.title, a.publish_time, a.image, a.profile, b.nickname as user, c.visit_count, d.comme_count, e.like_count FROM qfeng_article a LEFT JOIN qfeng_user b ON a.user=b.id LEFT JOIN (select article_id, count(1) as visit_count from qfeng_visit_record group by article_id) c ON a.id=c.article_id LEFT JOIN (select article_id, count(1) as comme_count from qfeng_comment where is_delete = 0 group by article_id) d ON a.id = d.article_id LEFT JOIN (select article_id, count(1) as like_count from qfeng_like where is_delete = 0 group by article_id) e ON a.id = e.article_id
RIGHT JOIN (select article_id from qfeng_ref_article_tag where tag = #{tag} where is_delete = 0) f on a.id = f.article_id
WHERE a.is_publish = 1 and a.is_delete =0 ORDER BY publish_time DESC limit #{pageSize} offset #{page}

mapper.update:
UPDATE qfeng_article
title = #{title}, publish_time = #{publishTime}, edition = #{edition}, front_content = #{frontContent}, backend_content = #{backendContent}, user = #{userId}, create_time = #{createTime}, modify_time = #{modifyTime}, is_publish = #{publish}, profile = #{profile},
WHERE id = #{id}
